import React from 'react';
import './CustomerManager.scss';
import api from "../../services/api";
import { Table, Button, Modal, ModalHeader, ModalBody, ModalFooter,
  Container, Col, Form,
  FormGroup, Label, Input,
   Alert } from 'reactstrap';
import Moment from 'react-moment';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
var DatePicker = require("reactstrap-date-picker");

class CustomerManager extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      customers : [],
      openConfirmDelete: false,
      openEdit: false,
      customerSelected: {},
      datepicker: new Date().toISOString()
    }
  }

  deleteRow = (customer) => {

    api.post("/customers/delete", customer).then((response) => {
      let customers = this.state.customers.filter(function(cust) { 
        return cust.email !== customer.email
      })
      this.setState({customers: customers});
      this.closeConfirmDialog();
    })
    .catch(function (error) {
      console.log(error);
    });


  }

  closeConfirmDialog = e => {
    this.setState({openConfirmDelete: false});
  }

  openConfirmDialog = e => {
    this.setState({openConfirmDelete: true, customerSelected: e});
  }

  openEditDialog = e => {
    this.setState({openEdit: true, customerSelected: e});
  }

  componentDidMount() {
      api.get("/customers").then((response) => {
        this.setState({"customers": response.data._embedded.customers});
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });
  }

  renderRow(customer) {
    return (
      <tr key={customer.email}>
        <td>{customer.name}</td>
        <td>{customer.email}</td>
        <td><Moment format="DD-MMM-YYYY">{customer.birthday}</Moment></td>
        <td className="editRow" ><FontAwesomeIcon icon={faEdit} /></td>
        <td className="editRow" onClick={() => this.openConfirmDialog(customer)}><FontAwesomeIcon icon={faTrash} /></td>
      </tr>
    );
  }

  render() {
    const {openConfirmDelete, customerSelected, openEdit, creatingNew, datepicker } = this.state;
    return (
        <div>
          <Button color="primary" className="addCustomer" onClick={this.openEditDialog}>Add</Button>{' '}

          <Table striped>
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Birthday</th>
                <th></th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              { this.state.customers.map(this.renderRow, this) }
            </tbody>
          </Table>

          <Modal isOpen={openConfirmDelete}>
            <ModalHeader >Confirm Delete</ModalHeader>
            <ModalBody>
              Are you sure to delete the customer {customerSelected.name}? 
            </ModalBody>
            <ModalFooter>
              <Button color="primary" onClick={() => this.deleteRow(customerSelected)}>Confirm</Button>{' '}
              <Button color="secondary" >Cancel</Button>
            </ModalFooter>
          </Modal>

          <Modal isOpen={openEdit}>
            <ModalHeader >{creatingNew ? 'New Customer': 'Edit Customer'}</ModalHeader>
            <ModalBody>
            <Form className="form" onSubmit={this.updateCustomer}>

          <Col>
            <FormGroup>
              <Label for="name">Name</Label>
              <Input
                type="text"
                name="name"
                id="name"
                placeholder="Name" required

              />
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label for="email">Email</Label>
              <Input
                type="text"
                id="email"
                placeholder="email" required
              />
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label for="birthday">Birthday</Label>
              <DatePicker id="datepicker" 
                    value= {datepicker} 
                    />
              <Input
                type="text"
                id="birthday"
                placeholder="Birthday" required
              />
            </FormGroup>
          </Col>
          <Button>Submit</Button>
        </Form>
            </ModalBody>
            <ModalFooter>
              <Button color="primary">Submit</Button>{' '}
              <Button color="secondary" >Cancel</Button>
            </ModalFooter>
          </Modal>


        </div>
    );
  }
}

export default CustomerManager;
